    "# Load required libraries\n",
    "library(ggridges)\n",
    "library(ggplot2)\n",
    "library(readr)\n",
    "\n",
    "# Load the Diamonds dataset\n",
    "diamonds <- read_tsv(\"/kaggle/input/54ksdiamonds/54KsDiamonds.txt\")\n",
    "head(diamonds)\n",
    "\n",
    "# Create a ridge plot for diamond prices across different cuts\n",
    "ggplot(diamonds, aes(x = price, y = cut, fill = cut)) +\n",
    "  geom_density_ridges() +\n",
    "  theme_ridges() + \n",
    "  theme(legend.position = \"none\")\n"
   ]
